  CONSTRAINT `tours_ibfk_1` FOREIGN KEY (`start_loc`) REFERENCES `locations` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=90 DEFAULT CHARSET=utf8;

